import { Handler, HookReturnType, UseScrollConfig } from '../types';
/**
 * @public
 *
 * Scroll hook.
 *
 * @param {Handler<'scroll'>} handler - the function fired every time the scroll gesture updates
 * @param {(Config | {})} [config={}] - the config object including generic options and scroll options
 * @returns {(...args: any[]) => HookReturnType<Config>}
 */
export declare function useScroll<Config extends UseScrollConfig>(handler: Handler<'scroll'>, config?: Config | {}): (...args: any[]) => HookReturnType<Config>;
